mapper.insert:
INSERT
  INTO TB_CLASS
 (
 	CLS_ID
  , MEMBER_ID
  , CLS_NAME
  , CLS_SUBJECT
  , CLS_STUNUM
  , CLS_GRADE
  , CLS_PAY
  , CLS_ROOM
  , CLS_START
  , CLS_END
  , CLS_NOTE
  , CLS_STATUS
 )
 VALUES
 (
    CLS_ID.NEXTVAL
 , #{ memberId }
 , #{ clsName }
 , #{ clsSubject }
 , #{ clsStuNum }
 , #{ clsGrade }
 , #{ clsPay }
 , #{ clsRoom }
 , #{ clsStart }
 , #{ clsEnd }
 , #{ clsNote }
 , 'N'
 )

mapper.select:
SELECT CLS_ID , CLS_SUBJECT , MEMBER_NAME , CLS_NAME , CLS_START , CLS_END , CLS_ROOM FROM (SELECT ROWNUM AS RNUM , A.CLS_ID , A.CLS_SUBJECT , D.MEMBER_NAME , A.CLS_NAME , A.CLS_START , A.CLS_END , A.CLS_ROOM FROM TB_CLASS A JOIN TB_MEMBER D ON (A.MEMBER_ID = D.MEMBER_ID) WHERE CLS_STATUS = 'N'
AND ROWNUM <= #{ pageNo } * #{ limit }
)
WHERE RNUM > (#{ pageNo } - 1) * #{ limit }

mapper.update:
UPDATE
	  TB_CLASS A
  SET   A.MEMBER_ID = #{ memberId } 
	  , A.CLS_NAME = #{ clsName }
	  , A.CLS_SUBJECT = #{ clsSubject }
	  , A.CLS_STUNUM = #{ clsStuNum }
	  , A.CLS_GRADE = #{ clsGrade }
	  , A.CLS_PAY = #{ clsPay }
	  , A.CLS_ROOM = #{ clsRoom }
	  , A.CLS_START = #{ clsStart }
	  , A.CLS_END = #{ clsEnd }
	  , A.CLS_NOTE = #{ clsNote }
 WHERE 	A.CLS_ID = #{ clsId }
   AND  A.CLS_STATUS = 'N'

UPDATE
	 TB_CLS_DAY
  SET CLS_DAY_ID = #{ clsDayId }
WHERE CLS_ID = #{ clsId }

 	UPDATE
 		 TB_CLS_TIME
 	  SET CLS_TIME_ID = #{ clsTimeId }
	WHERE CLS_ID = #{ clsId }
