mapper.insert:
INSERT
  INTO TB_CLASS
 (
 	CLS_ID
  , MEMBER_ID
  , CLS_NAME
  , CLS_SUBJECT
  , CLS_STUNUM
  , CLS_GRADE
  , CLS_PAY
  , CLS_ROOM
  , CLS_START
  , CLS_END
  , CLS_NOTE
  , CLS_STATUS
 )
 VALUES
 (
    CLS_ID.NEXTVAL
 , #{ member.id }
 , #{ clsName }
 , #{ clsSubject }
 , #{ clsStuNum }
 , #{ clsGrade }
 , #{ clsPay }
 , #{ clsRoom }
 , #{ clsStart }
 , #{ clsEnd }
 , #{ clsNote }
 , 'N'
 )

mapper.update:
UPDATE
	  TB_CLASS A
  SET   A.MEMBER_ID = (SELECT B.MEMBER_ID FROM TB_MEMBER B WHERE B.MEMBER_ID = #{member.id} )
	  , A.CLS_NAME = #{ clsName }
	  , A.CLS_SUBJECT = #{ clsSubject }
	  , A.CLS_STUNUM = #{ clsStuNum }
	  , A.CLS_GRADE = #{ clsGrade }
	  , A.CLS_PAY = #{ clsPay }
	  , A.CLS_ROOM = #{ clsRoom }
	  , A.CLS_START = #{ clsStart }
	  , A.CLS_END = #{ clsEnd }
	  , A.CLS_NOTE = #{ clsNote }
 WHERE 	A.CLS_ID = #{ clsId }
   AND  A.CLS_STATUS = 'N'

UPDATE
	 TB_CLANDDA
  SET CLS_DAY_ID = #{ clsDayId }
WHERE CLS_ID = #{ clsId }

 	UPDATE
 		 TB_CLANDTI
 	  SET CLS_TIME_ID = #{ clsTimeId }
	WHERE CLS_ID = #{ clsId }
