mapper.select:
SELECT CON_NO , CON_WRITE_DATE , CON_HTITLE , CON_HCONTENT FROM (SELECT /*+INDEX_DESC(C PK_TB_CONSULTANT)*/ ROWNUM AS RNUM , CON_NO , CON_WRITE_DATE , CON_HTITLE , CON_HCONTENT FROM TB_CONSULTANT C WHERE CON_HDELETE = 'N' AND MEMBER_ID = #{ memberId }
AND ROWNUM <= #{ pageNo } * #{ limit }
AND CON_HTITLE LIKE '%' || #{ keyword } || '%'
AND CON_HCONTENT LIKE '%' || #{ keyword } || '%'
AND CON_HDELETE = 'N'
)
WHERE RNUM > (#{ pageNo } - 1) * #{ limit }

mapper.insert:
INSERT
  INTO TB_CONSULTANT
(
  CON_NO
, MEMBER_ID
, CON_WRITE_DATE
, CON_HDATE
, CON_HTIME
, CON_HTITLE
, CON_HCONTENT
)
VALUES
(
  SEQ_CON_NO.NEXTVAL
, #{ memberId }
, SYSDATE
, #{ conHDate }
, #{ conHTime }
, #{ conHTitle }
, #{ conHContent }
)

mapper.update:
UPDATE
	   TB_CONSULTANT
   SET CON_HDATE = #{ conHDate }
     , CON_HTIME = #{ conHTime }
     , CON_HTITLE = #{ conHTitle }
     , CON_HCONTENT = #{ conHContent }
     , CON_HUPDATE = SYSDATE
 WHERE CON_NO = #{ no }
