mapper.insert:
INSERT
  INTO TB_MEMBER
(
  MEMBER_ID
, MEMBER_NAME
, MEMBER_PWD
, TEMP_PWD_YN
, PHONE
, EMAIL
, BIRTHDAY
, ADDRESS
, GENDER
, ENROLL_DATE
, DEL_DATE
, MEMBER_ST
)
VALUES
(
  #{ id }
, #{ name }
, #{ password }
, 'Y'
, #{ phone }
, #{ email }
, #{ birthDay }
, #{ address }
, #{ gender }
, #{ enrollDate }
, #{ delDate }
, #{ status }
)

mapper.select:
SELECT MEMBER_ID
  FROM TB_MEMBER
 WHERE MEMBER_NAME = #{ name }
   AND EMAIL = #{ email }

SELECT 

WHERE A.MEMBER_ID = #{ id }
  AND A.MEMBER_NAME = #{ name }
  AND A.EMAIL = #{ email }

mapper.insert:
	INSERT
	  INTO TB_FILE
