	 VALUES(
	 	SEQ_FILE_NO.NEXTVAL
	 , #{ fileOrginName }
	 , #{ fileSaveName }
	 , #{ filePath }
	 , #{ fileType }
	 , null
	 , null
	 )

INSERT 
  INTO TB_MEMBER_ROLE
  VALUES 
  (
    #{ role }
  , #{ id }
  )

mapper.update:
UPDATE TB_MEMBER
   SET MEMBER_NAME = #{ name }
	 , PHONE = #{ phone }
	 , EMAIL = #{ email }
	 , BIRTHDAY = #{ birthDay }
	 , ADDRESS = #{ address }
	 , GENDER = #{ gender }
	 , ENROLL_DATE = #{ enrollDate }
	 , MEMBER_ST = #{ status }
	 , DEL_DATE = #{ delDate }
WHERE MEMBER_ID = #{ id }

UPDATE TB_PROFILE
   SET FILE_ID = SEQ_FILE_NO.CURRVAL
 WHERE MEMBER_ID= #{ id }

mapper.select:
SELECT COUNT(*)
  FROM TB_PROFILE
 WHERE MEMBER_ID = #{ id }

mapper.update:
UPDATE TB_MEMBER
   SET MEMBER_ST = #{ status }
 	  , DEL_DATE = SYSDATE
 WHERE MEMBER_ID = #{ id }
