mapper.select:
SELECT NOTI_NO , NOTI_TITLE , NOTI_DATE , NOTI_VIEW FROM (SELECT /*+INDEX_DESC(N PK_TB_NOTICE)*/ ROWNUM AS RNUM , NOTI_NO , NOTI_TITLE , NOTI_DATE , NOTI_VIEW FROM TB_NOTICE N WHERE NOTI_DELETE = 'N'
AND ROWNUM <= #{ pageNo } * #{ limit }
AND NOTI_TITLE LIKE '%' || #{ keyword } || '%'
)
WHERE RNUM > (#{ pageNo } - 1) * #{ limit }

mapper.update:
UPDATE 
	   TB_NOTICE
   SET NOTI_TITLE = #{ notiTitle }
	 , NOTI_CONTENT	= #{ notiContent }
	 , NOTI_UPDATE = SYSDATE
 WHERE NOTI_NO = #{ no }
