  INTO TB_STUDENT
(
  MEMBER_ID
, STUDENT_TYPE
, SCHOOL_NAME
, SCHOOL_GRADE
, SCHOOL_CLASS
, CONSULT
)
VALUES
(
  #{ id }
, #{ studentType }
, #{ schoolName }
, #{ schoolGrade }
, #{ schoolClass }
, #{ consult }
)

